mapper.select:
SELECT  sp.*FROM ld_si_product sp,ld_si_product_detail spd WHERE sp.product_id=spd.product_id
 ORDER BY sp.create_time DESC, borrow_interest DESC, borrow_days ASC,spd.total DESC
 limit #{startRow},#{pageSize}

SELECT borinv.create_time,borinv.investor_capital as investorcapital, borinv.investor_interest as investorinterest ,date_add(borinv.create_time, interval product.borrow_days+1 day) as deadline, product.product_id,product.product_name,product.borrow_interest_rate,product.borrow_days FROM ld_si_product product,ld_borrow_investor borinv WHERE product.product_id=borinv.product_id AND investor_uid=#{investor_uid}
AND borinv.`status` = #{status}
AND date(borinv.create_time) = curdate()
AND borinv.create_time >= date_add(NOW(), interval -1 week)
AND borinv.create_time >= date_add(NOW(), interval -1 month)
AND borinv.create_time>= date_add(NOW(), interval -1 year)
ORDER BY borinv.create_time DESC limit #{startRow},#{pageSize}

SELECT count(1) FROM ld_si_product sipro,ld_borrow_investor borinv WHERE sipro.product_id=borinv.product_id AND investor_uid=#{investor_uid}
AND borinv.`status` = #{status}
AND date(borinv.create_time) = curdate()
AND borinv.create_time >= date_add(NOW(), interval -1 week)
AND borinv.create_time >= date_add(NOW(), interval -1 month)
AND borinv.create_time>= date_add(NOW(), interval -1 year)

SELECT borinv.create_time,borinv.investor_capital as investorcapital, borinv.investor_interest as investorinterest ,date_add(borinv.create_time, interval product.borrow_days+1 day) as deadline, product.product_id,product.product_name,product.borrow_interest_rate,product.borrow_days FROM ld_newhand_product product,ld_borrow_investor borinv WHERE product.product_id=borinv.product_id AND investor_uid=#{investor_uid}
AND borinv.`status` = #{status}
ORDER BY borinv.create_time DESC

SELECT borinv.create_time,borinv.investor_capital as investorcapital, borinv.investor_interest as investorinterest ,date_add(borinv.create_time, interval product.borrow_days+1 day) as deadline, product.product_id,product.product_name,product.borrow_interest_rate,product.borrow_days FROM ld_experience_product product,ld_borrow_investor borinv WHERE product.product_id=borinv.product_id AND investor_uid=#{investor_uid}
AND borinv.`status` = #{status}
ORDER BY borinv.create_time DESC

